import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  constructor() { }

  ngOnInit() {
  }

}
<!DOCTYPE html>
<nav>
  <a class="brand" routerLink="/">
    <img src="/assets/evo.png" alt="EvoSystems">
    <h2>Evo Systems</h2>
  </a>
  <ul class="links">
    <li><a routerLink="">Departamentos</a></li>
    <li><a routerLink="funcionarios">Funcionários</a></li>
  </ul>
</nav>
